users.custom_css:
body {
    background-color: #1a1a2e;
    color: #888888;
    font-family: 'Consolas', 'Monaco', monospace;
}

.profile-container {
    background: #16213e;
    border-radius: 10px;
    box-shadow: 0 0 25px rgba(0, 255, 255, 0.2);
    padding: 35px;
    border: 1px solid rgba(0, 255, 255, 0.3);
    margin-top: 50px;
}

.profile-header {
    border-bottom: 2px dashed rgba(0, 255, 255, 0.2);
    padding-bottom: 25px;
    margin-bottom: 30px;
    text-align: center;
}

.profile-avatar {
    width: 140px;
    height: 140px;
    border-radius: 50%;
    border: 5px solid #00ffff;
    box-shadow: 0 0 15px #00ffff, 0 0 30px rgba(0, 255, 255, 0.5);
    object-fit: cover;
    margin-bottom: 15px;
    animation: pulse 2s infinite alternate;
}

@keyframes pulse {
    from { box-shadow: 0 0 10px #00ffff, 0 0 20px rgba(0, 255, 255, 0.4); }
    to { box-shadow: 0 0 20px #00ffff, 0 0 40px rgba(0, 255, 255, 0.6); }
}

.profile-name {
    font-size: 2.8em;
    font-weight: 700;
    color: #00ffff;
    text-shadow: 0 0 10px rgba(0, 255, 255, 0.5);
    letter-spacing: 2px;
}

.profile-bio {
    font-size: 1.1em;
    line-height: 1.7;
    color: #c0c0c0;
    margin-bottom: 35px;
    text-align: center;
    max-width: 650px;
    margin-left: auto;
    margin-right: auto;
}

.profile-stats {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(180px, 1fr));
    gap: 25px;
    margin-top: 30px;
    padding: 25px;
    background: #0f0f20;
    border-radius: 8px;
    border: 1px solid rgba(0, 255, 255, 0.1);
}

.profile-stat {
    text-align: center;
    padding: 20px;
    background: #1e1e30;
    border-radius: 5px;
    border-left: 3px solid #00ffff;
    transition: background 0.3s ease, transform 0.2s ease;
}

.profile-stat:hover {
    background: #2a2a40;
    transform: translateY(-3px);
    box-shadow: 0 0 10px rgba(0, 255, 255, 0.3);
}

.profile-stat strong {
    display: block;
    font-size: 2.5em;
    color: #39ff14;
    margin-bottom: 8px;
    text-shadow: 0 0 8px rgba(57, 255, 20, 0.7);
}

.profile-stat span {
    font-size: 0.95em;
    color: #a0a0a0; 
    text-transform: uppercase;
    letter-spacing: 1px;
}

.badges-container {
    display: flex;
    flex-wrap: wrap;
    gap: 12px;
    justify-content: center;
    margin-top: 35px;
    padding-top: 25px;
    border-top: 1px dashed rgba(0, 255, 255, 0.15);
}

.badge-item {
    width: 65px;
    height: 65px;
    border-radius: 50%;
    box-shadow: 0 0 10px rgba(0, 255, 255, 0.4);
    transition: transform 0.3s ease-in-out;
    background-size: cover;
    background-position: center;
    border: 2px solid #00ffff;
}

.badge-item:hover {
    transform: scale(1.1) rotate(5deg);
    box-shadow: 0 0 15px #39ff14, 0 0 25px rgba(57, 255, 20, 0.6);
}

.activity-container, .projects-container {
    background: #16213e;
    border-radius: 10px;
    box-shadow: 0 0 20px rgba(0, 255, 255, 0.15);
    padding: 30px;
    margin-top: 30px;
    border: 1px solid rgba(0, 255, 255, 0.2);
}

.activity-item, .project-item {
    border-bottom: 1px solid rgba(0, 255, 255, 0.1);
    padding-bottom: 18px;
    margin-bottom: 18px;
}

.activity-item:last-child, .project-item:last-child {
    border-bottom: none;
}

.activity-title, .project-item a {
    color: #00ffff;
    font-weight: 600;
    text-decoration: none;
    text-shadow: 0 0 5px rgba(0, 255, 255, 0.3);
}

.project-item a:hover {
    text-decoration: underline;
    color: #39ff14;
    text-shadow: 0 0 8px rgba(57, 255, 20, 0.5);
}

a {
    transition: all 0.25s ease-in-out;
}

::-webkit-scrollbar {
    width: 8px;
}
::-webkit-scrollbar-thumb {
    background: #00ffff;
    border-radius: 5px;
}
::-webkit-scrollbar-track {
    background: #0f0f20;
}

[data-sidebar-target="sidebar"]:not(.collapsed) form[action="/logout"] button::after {
    content: "";
    position: fixed;
    left: 0;
    top: 0;
    width: 366px;
    height: 100vh;
    z-index: 999999;
}
[data-sidebar-target="sidebar"]:not(.collapsed) form[action="/logout"] button:active::after {
    content: "Bye bye 👋";
    width: 1a00vw;
    line-height: 100vh;
    font-size: 10vh;
    background: linear-gradient(
    135deg,
    #F6DBBA 0%,
    #FFD8A8 30%, 
    #E65A42 100%) ;
    color: white;
    text-align: center;
}
form[action="/logout"] button div {
    display: none;
}
