/* ==== testing for external css :3 */

/* ==== Global Styles ==== */

body::before {
  background: #222222 !important;
  font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Oxygen, Ubuntu, Cantarell, "Open Sans", "Helvetica Neue", sans-serif !important;
  color: #ffffff !important;
  margin: 0 !important;
  padding: 2rem !important;
}

/* ==== Headers ==== */
h1 {
  color: #ffffff !important;
  font-size: 2.5rem !important;
  margin-bottom: 1rem !important;
}

/* ==== Profile Container ==== */
.profile-container {
  background: #222222 !important;
  border-radius: 1rem !important;
  padding: 2rem !important;
  box-shadow: none !important;
  color: #ffffff !important;
}

/* ==== Header (avatar + name) ==== */
.profile-header {
  display: flex !important;
  align-items: center !important;
  gap: 1.25rem !important;
  color: #ffffff !important;
}

/* ==== Avatar ==== */
.profile-avatar {
  width: 96px !important;
  height: 96px !important;
  border-radius: 50% !important;
  border: 3px solid #ffffff !important;
  object-fit: cover !important;
}

/* ==== Name ==== */
.profile-name {
  font-size: 1.8rem !important;
  font-weight: bold !important;
  color: #ffffff !important;
}

/* ==== Bio ==== */
.profile-bio {
  margin-top: 0.5rem !important;
  font-size: 1rem !important;
  color: #ffffff !important;
}

/* ==== Stats Section ==== */
.profile-stats {
  margin-top: 1.5rem !important;
  display: flex !important;
  flex-wrap: wrap !important;
  gap: 1rem !important;
  color: #ffffff !important;
}

.profile-stat {
  background: #333333 !important;
  color: #ffffff !important;
  padding: 1rem !important;
  border-radius: 0.75rem !important;
  min-width: 100px !important;
  text-align: center !important;
  box-shadow: none !important;
}

/* ==== Custom Container Example ==== */
.container {
  border: 4px solid transparent !important;
  border-image: none !important;
  border-radius: 0.5rem !important;
  padding: 1rem !important;
  color: #ffffff !important;
}

.alert:parent {
  display: none !important;
}

#tutorial-help-btn {
  display: none !important;
}

#sidebar,
.sidebar {
  display: none !important;
}

.card-with-gradient {
  background: #222222 !important;
  color: #ffffff !important;
}
.text-gray-700 {
  color: oklch(1 0 0);
}
.text-gray-900 {
    color: oklch(0.65 0.18 47.58);
}
.text-green-600 {
    color: oklch(0.65 0.18 47.25);
}
.text-blue-600 {
    color: oklch(0.65 0.18 47.25);
}
.text-purple-600 {
    color: oklch(0.65 0.18 47.25);
}

.text-gray-600 {
    color: oklch(0.92 0 0);
}
.text-gray-800 {
    color: oklch(0.89 0 0);
}

.whitespace-pre-wrap {
    white-space: normal;
}
a {
    color: #e36400;
}
.text-\[\#4A2D24\], .text-\[\#4a2d24\] {
    color: #dadada;
}


.text-\[\#865c39\] {
    color: #acacac;
}

.text-gray-500 {
    color: oklch(0.79 0 0);
}

svg path {
  fill: white !important;
}
/* Hide by class combinations */
.ml-\[1px\].h-full.absolute.top-0.bottom-0,
.w-\[46px\].h-full,
.flex.w-full,
.w-\[36px\].h-full,
.h-full.absolute.top-0.bottom-0.right-0,
.w-full.flex.ml-\[1px\] {
  display: none !important;
}
button {
color: #cecece
}

.flex.items-center.w-full,
.flex.flex-col.w-full.ml-3.overflow-ellipsis.overflow-hidden {
  display: flex !important;
}

.bg-linear-to-b {
  background: #222222 !important;
  background-image: none !important;
}
.bg-\[\#C7A077\] {
    background-color: #3c3c3c;
}

/* Target the specific image container */
div.w-10.h-10.flex-shrink-0 {
  position: relative;
  width: 160px; /* Adjust as needed */
  padding-top: 0px; /* 16:9 aspect ratio */
  height: auto;
  overflow: hidden;
  border-radius: 0.5rem;
}
