users.custom_css:
body::before {
  display: none !important;
}
body {
  background-color: #0c0e10 !important;
text-color: #cbd5e1 !important;
}

.profile-name {
  color: #ffffff; /* Tailwind's gray-500 */
  font-size: 3rem; /* Base size (text-5xl) */
  font-weight: 800; /* font-extrabold */
  letter-spacing: -0.02em; /* tighter spacing */
  line-height: 1.2; /* Tailwind's leading-tight */
  font-family: system-ui, -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif;
  margin: 1rem 0;
  transition: color 0.4s cubic-bezier(.4,0,.2,1);
}

button {
  background: transparent;
  border: 2px solid #fff;
  color: #fff;
  padding: 12px 32px;
  border-radius: 10px; /* Less rounded, more straight */
  font-size: 1.1rem;
  cursor: pointer;
  transition: 
    border-color 0.2s,
    color 0.2s,
    box-shadow 0.2s;
  outline: none;
}

.text-gray-800 {
  color: #7dd3fc !important;
  text-shadow: 0 0 20px rgba(125, 211, 252, 0.3);
  font-family: system-ui, -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif;
  transition: color 0.4s cubic-bezier(.4,0,.2,1);
}

.card-with-gradient {
  position: relative;
  background: rgba(255, 255, 255, 0.03);
  border: 1.5px solid #444;
  border-radius: 14px;
  padding: 32px 36px;
  max-width: 420px;
  width: 100%;
  min-width: 280px;
  color: #fff;
  box-shadow: 0 2px 16px 0 rgba(0, 0, 0, 0.08);
  transition: border-color 0.2s, box-shadow 0.2s, background 0.2s;
  font-family: system-ui, -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif;
  font-size: 1.08rem;
  line-height: 1.6;
  letter-spacing: -0.01em;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  gap: 18px;
  flex: 1 1 320px;
  box-sizing: border-box;
  overflow: hidden;
  z-index: 0;
}

.card-with-gradient {
  position: relative !important;
  background: rgba(255, 255, 255, 0.03) !important;
  border: 1.5px solid #444 !important;
  border-radius: 14px !important;
  padding: 32px 36px !important;
  max-width: 420px !important;
  width: 100% !important;
  min-width: 280px !important;
  color: #fff !important;
  box-shadow: 0 2px 16px 0 rgba(0, 0, 0, 0.08) !important;
  transition: border-color 0.2s, box-shadow 0.2s, background 0.2s !important;
  font-family: system-ui, -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif !important;
  font-size: 1.08rem !important;
  line-height: 1.6 !important;
  letter-spacing: -0.01em !important;
  display: flex !important;
  flex-direction: column !important;
  align-items: flex-start !important;
  gap: 18px !important;
  flex: 1 1 320px !important;
  box-sizing: border-box !important;
  overflow: hidden !important;
  z-index: 0 !important;
}

.card-with-gradient::before {
  content: "" !important;
  position: absolute !important;
  top: -50% !important;
  left: -50% !important;
  width: 200% !important;
  height: 200% !important;
  background: linear-gradient(130deg, #7dd3fc 0%, #a78bfa 50%, #38bdf8 100%) !important;
  background-size: 300% 300% !important;
  animation: gradientMove 8s ease infinite !important;
  opacity: 0.08 !important;
  z-index: -1 !important;
  filter: blur(40px) !important;
}


h1, .heading-1 {
  color: #ffffff !important;
  font-size: 2.5rem !important;
  font-weight: 700 !important;
  letter-spacing: -0.015em !important;
  line-height: 1.2 !important;
  font-family: system-ui, -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif !important;
  margin: 1rem 0 !important;
  transition: color 0.4s cubic-bezier(.4,0,.2,1) !important;
}

h2, .heading-2 {
  color: #7dd3fc !important;
  font-size: 1.8rem !important;
  font-weight: 600 !important;
  letter-spacing: -0.01em !important;
  line-height: 1.35 !important;
  font-family: system-ui, -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif !important;
  margin: 0.75rem 0 !important;
  text-shadow: 0 0 12px rgba(125, 211, 252, 0.2) !important;
  transition: color 0.4s cubic-bezier(.4,0,.2,1) !important;
}

li {
  color: #cbd5e1 !important; /* Tailwind slate-300 */
  font-size: 1.05rem !important;
  line-height: 1.6 !important;
  margin-left: 1.25rem !important;
  padding-left: 0.5rem !important;
  position: relative !important;
  font-family: system-ui, -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif !important;
}

li::before {
  content: "•" !important;
  position: absolute !important;
  left: 0 !important;
  color: #7dd3fc !important;
  font-size: 1.2rem !important;
  line-height: 1 !important;
}


p {
  color: #cbd5e1 !important; /* Tailwind slate-300 */
  font-size: 1.05rem !important;
  line-height: 1.6 !important;
  margin-left: 1.25rem !important;
  padding-left: 0.5rem !important;
  position: relative !important;
  font-family: system-ui, -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif !important;
}
