users.custom_css:
body {
  min-height: 100vh;
  background: linear-gradient(135deg,#ff6b6b,#f7d716,#4ecdc4,#5f27cd,#ff6b6b);
  background-size: 400% 400%;
  animation: bgmove 8s linear infinite, bodyhue 3s linear infinite;
  font-family: 'Comic Sans MS', cursive, sans-serif;
  overflow-x: hidden;
}
@keyframes bgmove {
  0%{background-position:0% 50%;}
  100%{background-position:100% 50%;}
}
@keyframes bodyhue {
  0%{filter:hue-rotate(0deg);}
  100%{filter:hue-rotate(360deg);}
}
.container,.profile-container {
  border:8px dashed;
  border-image: repeating-linear-gradient(90deg,#ff6b6b 0 10px,#f7d716 10px 20px,#4ecdc4 20px 30px,#5f27cd 30px 40px) 8;
  border-radius:32px;
  box-shadow:0 0 40px 10px #fff7,0 0 0 8px #0002 inset;
  animation: borderwob 1.5s infinite linear;
}
@keyframes borderwob {
  0%,100%{border-radius:32px;}
  50%{border-radius:64px 16px 64px 16px;}
}
.profile-header {
  text-align:center;
  padding:2rem 0 1rem 0;
  background:linear-gradient(90deg,#f7d716,#ff6b6b,#4ecdc4,#5f27cd);
  background-size:200% 100%;
  animation: headrain 3s linear infinite, headskew 1.2s infinite alternate;
  border-radius:0 0 48px 48px;
  box-shadow:0 8px 32px #0003;
  position:relative;
}
@keyframes headrain {
  0%{background-position:0% 50%;}
  100%{background-position:100% 50%;}
}
@keyframes headskew {
  0%{transform:skewX(0deg);}
  100%{transform:skewX(10deg);}
}
.profile-header::after {
  content:'✨';
  position:absolute;right:10px;top:10px;font-size:2em;animation:sparkle 1s infinite alternate;
}
@keyframes sparkle {
  0%{opacity:.3;transform:scale(1);}
  100%{opacity:1;transform:scale(1.5);}
}
.profile-avatar {
  width:120px;height:120px;border-radius:50%;
  border:6px solid #fff;box-shadow:0 0 0 8px #f7d716,0 0 32px 8px #4ecdc4;
  animation: avaspin 2s linear infinite, avabounce 1.2s ease-in-out infinite, avahue 3s linear infinite;
  display:block;margin:0 auto 1rem auto;object-fit:cover;
}
@keyframes avaspin {
  0%{transform:rotate(0deg);}
  100%{transform:rotate(360deg);}
}
@keyframes avabounce {
  0%,100%{transform:translateY(0);}
  50%{transform:translateY(-18px) scale(1.1);}
}
@keyframes avahue {
  0%{filter:hue-rotate(0deg);}
  100%{filter:hue-rotate(360deg);}
}
.profile-name {
  font-size:2.2rem;font-weight:bold;color:#fff;
  text-shadow:0 0 8px #5f27cd,0 2px 8px #0008;
  animation: namewob 1.1s infinite alternate, namerain 2s linear infinite;
  letter-spacing:.1em;
}
@keyframes namewob {
  0%{transform:skewX(0deg);}
  25%{transform:skewX(8deg);}
  50%{transform:skewX(-8deg);}
  75%{transform:skewX(4deg);}
  100%{transform:skewX(0deg);}
}
@keyframes namerain {
  0%{filter:hue-rotate(0deg);}
  100%{filter:hue-rotate(360deg);}
}
.profile-bio {
  font-size:1.1rem;
  background:linear-gradient(90deg,#f7d716,#4ecdc4,#ff6b6b,#5f27cd);
  background-size:300% 100%;
  -webkit-background-clip:text;
  -webkit-text-fill-color:transparent;
  animation: biocolor 3s linear infinite, biotype 3s steps(40) 1s 1 normal both, bioshake 1.2s infinite alternate;
  border-radius:16px;padding:.5em 1em;margin:1em auto;max-width:500px;
  box-shadow:0 2px 16px #4ecdc488;border:2px solid #fff4;position:relative;
}
@keyframes biocolor {
  0%{background-position:0% 50%;}
  100%{background-position:100% 50%;}
}
@keyframes biotype {
  from{width:0;}
  to{width:100%;}
}
@keyframes bioshake {
  0%,100%{transform:rotate(-2deg);}
  50%{transform:rotate(2deg);}
}
.profile-bio::after {
  content:'💥';
  position:absolute;right:8px;top:8px;font-size:1.2em;animation:sparkle 1s infinite alternate;
}
.profile-stats {
  display:flex;justify-content:center;gap:2rem;margin:2rem 0;flex-wrap:wrap;
}
.profile-stat {
  background:linear-gradient(135deg,#ff6b6b,#f7d716,#4ecdc4,#5f27cd);
  background-size:200% 200%;
  animation: statbg 2s ease-in-out infinite alternate, statfloat 2.2s ease-in-out infinite, statspin 3s linear infinite;
  color:#fff;font-size:1.2rem;font-weight:bold;border-radius:24px;
  box-shadow:0 4px 24px #0004,0 0 0 4px #fff8 inset;
  padding:1.2em 2em;border:3px solid #fff;position:relative;transition:transform .2s;cursor:pointer;
}
.profile-stat:hover {
  transform:scale(1.15) rotate(-6deg);
  box-shadow:0 8px 32px #f7d71688,0 0 0 8px #4ecdc4 inset;
}
@keyframes statbg {
  0%{background-position:0% 0%;}
  100%{background-position:100% 100%;}
}
@keyframes statfloat {
  0%,100%{top:0;}
  50%{top:-18px;}
}
@keyframes statspin {
  0%{filter:hue-rotate(0deg);}
  100%{filter:hue-rotate(360deg);}
}
h1,h2,h3,h4,h5,h6 {
  color:#2c3e50;
  font-family:'Comic Sans MS',cursive,sans-serif;
  text-shadow:2px 2px 0 #f7d716,4px 4px 0 #4ecdc4,6px 6px 0 #ff6b6b,8px 8px 0 #5f27cd;
  animation: headshadow 1.2s infinite alternate, headshake 1.5s infinite alternate;
}
@keyframes headshadow {
  0%{text-shadow:2px 2px 0 #f7d716,4px 4px 0 #4ecdc4,6px 6px 0 #ff6b6b,8px 8px 0 #5f27cd;}
  50%{text-shadow:4px 4px 0 #ff6b6b,2px 2px 0 #5f27cd,8px 8px 0 #f7d716,6px 6px 0 #4ecdc4;}
  100%{text-shadow:2px 2px 0 #f7d716,4px 4px 0 #4ecdc4,6px 6px 0 #ff6b6b,8px 8px 0 #5f27cd;}
}
@keyframes headshake {
  0%,100%{transform:skewX(0deg) scale(1);}
  50%{transform:skewX(10deg) scale(1.1);}
}
button,.btn {
  background:linear-gradient(90deg,#ff6b6b,#f7d716,#4ecdc4,#5f27cd);
  background-size:200% 100%;
  color:#fff;font-size:1.1rem;font-weight:bold;border:none;
  border-radius:16px;padding:.7em 2em;
  box-shadow:0 2px 16px #5f27cd88,0 0 0 4px #fff8 inset;
  cursor:pointer;
  animation: btnglow 1.5s infinite alternate, btnbgmove 2s linear infinite, btnspin 2.5s linear infinite;
  transition:transform .2s;
}
button:hover,.btn:hover {
  transform:scale(1.12) rotate(2deg);
  box-shadow:0 8px 32px #f7d71688,0 0 0 8px #4ecdc4 inset;
  animation: btnjelly .4s, btnspin 1s linear infinite;
}
@keyframes btnglow {
  0%{box-shadow:0 2px 16px #5f27cd88,0 0 0 4px #fff8 inset;}
  100%{box-shadow:0 8px 32px #f7d71688,0 0 0 8px #4ecdc4 inset;}
}
@keyframes btnbgmove {
  0%{background-position:0% 50%;}
  100%{background-position:100% 50%;}
}
@keyframes btnspin {
  0%{filter:hue-rotate(0deg);}
  100%{filter:hue-rotate(360deg);}
}
@keyframes btnjelly {
  0%{transform:scale(1,1);}
  30%{transform:scale(1.25,.75);}
  40%{transform:scale(.75,1.25);}
  50%{transform:scale(1.15,.85);}
  65%{transform:scale(.95,1.05);}
  75%{transform:scale(1.05,.95);}
  100%{transform:scale(1,1);}
}
::-webkit-scrollbar {
  width:14px;background:#4ecdc4;
}
::-webkit-scrollbar-thumb {
  background:linear-gradient(135deg,#ff6b6b,#f7d716,#5f27cd);
  border-radius:8px;box-shadow:0 0 8px #fff8;
  animation: scrollblink 1.2s infinite alternate;
}
@keyframes scrollblink {
  0%{opacity:1;}
  100%{opacity:.6;}
}
a {
  color:#5f27cd;text-decoration:underline wavy #f7d716;font-weight:bold;
  transition:color .2s;
  animation: linkcolor 1.2s infinite alternate, linkwiggle .7s infinite;
  display:inline-block;
}
a:hover {
  color:#ff6b6b;text-decoration:underline wavy #4ecdc4;
  animation: linkcolor .6s infinite alternate, linkwiggle .3s infinite;
}
@keyframes linkcolor {
  0%{color:#5f27cd;}
  50%{color:#f7d716;}
  100%{color:#4ecdc4;}
}
@keyframes linkwiggle {
  0%,100%{transform:rotate(-4deg) scale(1);}
  50%{transform:rotate(4deg) scale(1.1);}
}
input,textarea,select {
  border:3px solid #4ecdc4;border-radius:12px;padding:.5em 1em;
  font-size:1.1rem;background:#fff9;box-shadow:0 2px 8px #ff6b6b44;
  transition:border-color .3s,box-shadow .3s;
  animation: inputpulse 1.5s infinite alternate, inputshake 1.2s infinite alternate;
}
input:focus,textarea:focus,select:focus {
  border-color:#f7d716;box-shadow:0 0 16px #f7d71688,0 0 0 4px #4ecdc4 inset;outline:none;
}
@keyframes inputpulse {
  0%{box-shadow:0 2px 8px #ff6b6b44;}
  100%{box-shadow:0 4px 16px #5f27cd44;}
}
@keyframes inputshake {
  0%,100%{transform:rotate(-2deg);}
  50%{transform:rotate(2deg);}
}
body::before,body::after {
  content:'';
  position:fixed;top:0;left:0;width:100vw;height:100vh;pointer-events:none;z-index:9999;
  background-image:
    radial-gradient(circle,#ff6b6b 8px,transparent 10px),
    radial-gradient(circle,#f7d716 6px,transparent 8px),
    radial-gradient(circle,#4ecdc4 10px,transparent 12px),
    radial-gradient(circle,#5f27cd 7px,transparent 9px);
  background-size:120px 120px,180px 180px,200px 200px,160px 160px;
  background-position:0 0,60px 90px,120px 60px,80px 120px;
  animation: confetti 10s linear infinite, confhue 5s linear infinite;
  opacity:.22;
}
body::after {animation-delay:5s;opacity:.13;}
@keyframes confetti {
  0%{background-position:0 0,60px 90px,120px 60px,80px 120px;}
  100%{background-position:0 100vh,60px 190vh,120px 160vh,80px 220vh;}
}
@keyframes confhue {
  0%{filter:hue-rotate(0deg);}
  100%{filter:hue-rotate(360deg);}
}
html {cursor:url('https://cdn.jsdelivr.net/gh/twitter/twemoji@14.0.2/assets/72x72/2728.png'),auto;}
